const _TEST: &'static str = "bvwbjplbgvbhsrlpgdmjqwftvncz";
const _PROD: &'static str = "";

const SIZE: usize = 14;

fn main() {
    let chars: Vec<char> = _PROD.chars().collect();

    let mut idx = 0;

    for window in chars.windows(SIZE) {
        idx += 1;

        let mut vec: Vec<&char> = window.into_iter().collect();

        let size_before = vec.len().clone();

        vec.sort();
        vec.dedup();

        if vec.len() == size_before {
            break;
        }
    }

    println!("{}", idx + (SIZE - 1));
}
